import React, { useState, useEffect } from 'react';

function MainGame() {
    const [characters, setCharacters] = useState([]);
    const [selectedCharacter, setSelectedCharacter] = useState('');
    const [feedback, setFeedback] = useState('');

    useEffect(() => {
        fetch('http://127.0.0.1:5000/api/characters')
            .then(response => response.json())
            .then(data => setCharacters(data.map(char => char.name))); // Adjust based on your API structure
    }, []);

    const handleCharacterChange = (event) => {
        setSelectedCharacter(event.target.value);
    };

    const handleGuessSubmit = () => {
        if (!selectedCharacter) {
            setFeedback('Please select a character.');
            return;
        }

        // Additional logic to validate the guess
        setFeedback('Guess submitted: ' + selectedCharacter);
        // Reset or handle the guess here
    };

    return (
        <div className="main-game">
            <select value={selectedCharacter} onChange={handleCharacterChange}>
                <option value="">Select a character</option>
                {characters.map((name, index) => (
                    <option key={index} value={name}>{name}</option>
                ))}
            </select>
            <button onClick={handleGuessSubmit}>Guess</button>
            <p>{feedback}</p>
        </div>
    );
}

export default MainGame;
