import React, { useState, useEffect } from 'react';
import './MainGame.css';

const CORRECT_ANSWER = "Zio"; // You can dynamically set this based on your application needs

const SOLUTION = {
  element: "dark",
  class: "mage",
  rarity: 5
};



function MainGame() {
  const [input, setInput] = useState('');
  const [guesses, setGuesses] = useState([]);
  const [feedback, setFeedback] = useState('');
  const [characters, setCharacters] = useState([]);
  const [filteredCharacters, setFilteredCharacters] = useState([]);
  const [solution, setSolution] = useState(null);  // State to hold the solution

  useEffect(() => {
      fetch('http://127.0.0.1:5000/api/characters')
          .then(response => response.json())
          .then(data => {
              const characterList = Object.keys(data).map(key => ({
                  id: data[key].id,
                  name: key,
                  attribute: data[key].attribute,
                  class: data[key].role, // Assuming role is equivalent to class
                  starsign: data[key].zodiac,
                  rarity: data[key].rarity,
                  photo: data[key].assets.icon
              }));
              setCharacters(characterList);
              // Set a random solution from the list
              if (characterList.length > 0) {
                  const randomIndex = Math.floor(Math.random() * characterList.length);
                  setSolution(characterList[randomIndex]);
              }
          });
  }, []);

  useEffect(() => {
    if (input.trim() === '') {
        setFilteredCharacters([]);
    } else {
        const filtered = characters.filter(char =>
            char.name.toLowerCase().includes(input.toLowerCase())
        );

        // Sort so that names starting with the input are at the top
        filtered.sort((a, b) => {
            const aStartsWith = a.name.toLowerCase().startsWith(input.toLowerCase());
            const bStartsWith = b.name.toLowerCase().startsWith(input.toLowerCase());
            if (aStartsWith && !bStartsWith) {
                return -1;  // a should come before b
            } else if (!aStartsWith && bStartsWith) {
                return 1;  // b should come before a
            } else {
                return 0;  // default order
            }
        });

        setFilteredCharacters(filtered);
      }
  }, [input, characters]);


  const handleInputChange = (event) => {
      setInput(event.target.value);
  };


    const handleGuessSubmit = async () => {
    if (input.trim() !== '' && solution) {
        // Check if the guessed character matches the solution character
        const isCorrect = input.trim().toLowerCase() === solution.name.toLowerCase();
        const newGuess = {
            photo: solution.photo,
            guess: input,
            element: solution.attribute,
            class: solution.class,
            starsign: solution.starsign,
            rarity: solution.rarity,
            correct: isCorrect
        };

        // Update feedback and guesses
        setGuesses(prevGuesses => [newGuess, ...prevGuesses]);
        setInput(''); // Clear input
        setFeedback(isCorrect ? 'Correct! Well done.' : 'Incorrect, try again!');
    } else {
        setFeedback('Please enter a valid character name.');
    }
};

     

  async function fetchCharacters() {
    const response = await fetch('http://127.0.0.1:5000/api/characters');
    const characters = await response.json();
    console.log(characters);
  }
  
  async function fetchCharacterDetails(characterId) {
    const response = await fetch(`http://127.0.0.1:5000/api/characters/${characterId}`);
    const details = await response.json();
    console.log(details); // Make sure this logs expected details
  }
  
  
  return (
    <div className="main-game">
        <div className="character-display">
        <input
            type="text"
            placeholder="Enter character name..."
            value={input}
            onChange={handleInputChange}
        />
        {filteredCharacters.length > 0 && (
            <ul className="autocomplete-dropdown">
                {filteredCharacters.map(character => (
                    <li key={character.id} onClick={() => setInput(character.name)}>
                        {character.name} {/*{photo} {name}*/}
                    </li>
                ))}
            </ul>
        )}
            <button onClick={handleGuessSubmit}>Guess</button>
        </div>
        <p>{feedback}</p> {/* Display feedback message */}
        <div className="guess-table">
            <table>
                <thead>
                    <tr>
                        <th>Photo</th>
                        <th>Name</th>
                        <th>Element</th>
                        <th>Class</th>
                        <th>Star Sign</th>
                        <th>Rarity</th>
                        <th>Status</th>
                    </tr>
                </thead>
                <tbody>
                    {guesses.map((item, index) => (
                        <tr key={index}>
                            <td>
                              <img src={item.photo} alt="Character" className="character-image" />
                            </td>
                            <td>{item.guess}</td>
                            <td className={item.element === SOLUTION.element ? 'correct-answer' : 'incorrect-answer'}>
                                {item.element}
                            </td>
                            <td className={item.class === SOLUTION.class ? 'correct-answer' : 'incorrect-answer'}>
                                {item.class}
                            </td>
                            <td className={item.starsign === SOLUTION.starsign ? 'correct-answer' : 'incorrect-answer'}>
                                {item.starsign}
                            </td>
                            <td className={item.rarity === SOLUTION.rarity ? 'correct-answer' : 'incorrect-answer'}>
                                {item.rarity}
                            </td>
                            <td className={item.correct ? 'correct' : 'incorrect'}>
                                {item.correct ? 'Correct' : 'Incorrect'}
                            </td>
                        </tr>
                    ))}
                </tbody>
            </table>
        </div>
    </div>
  );

}

export default MainGame;
