import React, { useState, useEffect } from 'react';

function MainGame() {
    const [characters, setCharacters] = useState([]);
    const [selectedCharacter, setSelectedCharacter] = useState('');

    useEffect(() => {
        fetch('http://127.0.0.1:5000/api/characters')
            .then(response => response.json())
            .then(data => {
                if (Array.isArray(data)) {
                    setCharacters(data); // Assuming data is an array of characters
                }
            })
            .catch(error => console.error('Error fetching characters:', error));
    }, []);

    const handleCharacterChange = (event) => {
        setSelectedCharacter(event.target.value);
    };

    return (
        <div>
            <select value={selectedCharacter} onChange={handleCharacterChange}>
                <option value="">Select a character</option>
                {characters.map((character, index) => (
                    <option key={index} value={character.name}>{character.name}</option>
                ))}
            </select>
        </div>
    );
}

export default MainGame;
