import React, { useState } from 'react';
import './MainGame.css';

const CORRECT_ANSWER = "Kirara"; // You can dynamically set this based on your application needs

function MainGame() {
  const [input, setInput] = useState('');
  const [guesses, setGuesses] = useState([]);
  const [feedback, setFeedback] = useState('');
  

    const handleInputChange = (e) => {
        setInput(e.target.value);
    };

    const handleGuessSubmit = () => {
      if (input.trim() !== '') {
          const isCorrect = input.trim().toLowerCase() === CORRECT_ANSWER.toLowerCase();
          const newGuess = {
              guess: input,
              correct: isCorrect
          };
          setGuesses([newGuess, ...guesses]); // Prepend new guess to the array
          setInput(''); // Clear input after adding guess
  
          // Set feedback message
          if (isCorrect) {
              setFeedback('Correct! Well done.');
          } else {
              setFeedback('Incorrect, try again!');
          }
      }
  };

  async function submitGuess(guess) {
    const response = await fetch('/guess', {
      method: 'POST',
      headers: {
        'Content-Type': 'application/json'
      },
      body: JSON.stringify({ guess })
    });
    const { correct } = await response.json();
    if (correct) {
      alert("Correct guess!");
    } else {
      alert("Wrong guess. Try again!");
    }
  }
  

  async function fetchCharacters() {
    const response = await fetch('/characters');
    const characters = await response.json();
    console.log(characters);
  }
  
  async function fetchCharacterDetails(characterId) {
    const response = await fetch(`/characters/${characterId}`);
    const details = await response.json();
    console.log(details);
  }
  
  
  

  return (
    <div className="main-game">
        <div className="character-display">
            <input
                type="text"
                placeholder="Enter character name..."
                value={input}
                onChange={handleInputChange}
            />
            <button onClick={fetchCharacters}>Guess</button>
        </div>
        <p>{feedback}</p> {/* Display feedback message */}
        <div className="guess-table">
            <table>
                <thead>
                    <tr>
                        <th>Guess Number</th>
                        <th>Guess</th>
                        <th>Status</th>
                    </tr>
                </thead>
                <tbody>
                    {guesses.map((item, index) => (
                        <tr key={index}>
                            <td>{index + 1}</td>
                            <td>{item.guess}</td>
                            <td className={item.correct ? 'correct' : 'incorrect'}>
                                {item.correct ? 'Correct' : 'Incorrect'}
                            </td>
                        </tr>
                    ))}
                </tbody>
            </table>
        </div>
    </div>
  );

}

export default MainGame;
